    val whenLoading = MutableLiveData<Boolean>()
    val whenDataLoadedFailure = MutableLiveData<String>()
    val whenOpenPdf = MutableLiveData<String>()
    val whenSyncSuccessData = MutableLiveData<MenuStepData>()
    val whenSyncFailureShowMsg = MutableLiveData<MenuStepData>()
    private val currentPDF = CacheManager.getCacheEContractQRCode()


    fun showPDF( refID: String)  {
        val docName = currentPDF?.doc_name ?: ""
        val docBase64 = currentPDF?.doc_base64 ?: ""

        var filename = docName  + "_" + refID

        if(docBase64 == "") {
            whenLoading.value = false
            whenDataLoadedFailure.value = ""
        }else {
            whenLoading.value = false
            PdfUtils.savePdf(filename, docBase64 ?: "")
            whenOpenPdf.postValue(filename)
        }
    }

